#include <iostream>
#include <array>
#include <algorithm>
int main() {
    std::array<int, 5> arr = {3, 1, 4, 1, 5};
    std::sort(arr.begin(), arr.end());
    for (int n : arr) {
        std::cout << n << ' ';
    }
    // Outputs: 1 1 3 4 5
}

int num = 2;
switch (num) {
    case 0:
        std::cout << "Zero";
        break;
    case 1:
        std::cout << "One";
        break;
    case 2:
        std::cout << "Two";
        break;
    default:
        std::cout << "What?";
        break;
}

int i = 0;
while (i < 6) {
    std::cout << i++;
} // Outputs: 012345

int i = 1;
do {
    std::cout << i++;
} while (i <= 5);
// Outputs: 12345

for (int i = 0; i < 10; i++) {
    if (i % 2 == 0) {
        continue;
    }
    std::cout << i;
} // Outputs: 13579

while (true) {
    std::cout << "infinite loop";
}

#include <iostream>
#include <array>
#include <algorithm>

int main() {
    auto print = [](int num) { std::cout << num << std::endl; };
    std::array<int, 4> arr = {1, 2, 3, 4};
    std::for_each(arr.begin(), arr.end(), print);
    return 0;
}

for (int n : {1, 2, 3, 4, 5}) {
