  public:
    int x;
  private:
    int y;
  protected:
    int z;
};
MyClass myObj;
myObj.x = 25;  // Allowed
myObj.y = 50;  // Not allowed
myObj.z = 75;  // Not allowed

class MyClass {
  private:
    int myNum;
  public:
    void setMyNum(int num) {
      myNum = num;
    }
    int getMyNum() {
      return myNum;
    }
};
MyClass myObj;
myObj.setMyNum(15);
cout << myObj.getMyNum() << endl;

class Vehicle {
  public:
    string brand = "Ford";
    void honk() { cout << "Tuut, tuut!" << endl; }
};
class Car : public Vehicle {
  public:
    string model = "Mustang";
};
Car myCar;
myCar.honk();
cout << myCar.brand + " " + myCar.model << endl;

