const rangeIterator = {
  start: 0,
  end: 5,
  [Symbol.iterator]() {
    let current =  this.start;
    return {
      next() {
        return current <= this.end
          ? { value: current++,   done: false }
          : { done: true };
      }
    };
  }
};

