const obj = { a: { b: { c: 1 } } };
const flatten = (obj, prefix = '') => 
  Object.keys(obj).reduce((acc, key) => {
    const prop = prefix ? `${prefix}.${key}` : key;
    return typeof obj[key] === 'object' ? { ...acc, ...flatten(obj[key], prop) } : { ...acc, [prop]: obj[key] };
  }, {});
console.log(flatten(obj)); // { 'a.b.c': 1 }

