example:
const numbers = [1, 2, 3, 4, 5, 6];
const evens = numbers.filter(num => num % 2 === 0);
console.log(evens); // Output: [2, 4, 6]

const numbers = [1, 2, 3, 4];
const doubled = numbers.map(num => num * 2);
console.log(doubled); // Output: [2, 4, 6, 8]

const numbers = [1, 2, 3, 4];
const sum = numbers.reduce((acc, num) => acc + num, 0);
console.log(sum); // Output: 10

const users = [{name: 'Alice'}, {name: 'Bob'}, {name: 'Charlie'}];
const user = users.find(u => u.name === 'Bob');
console.log(user); // Output: {name: 'Bob'}

const numbers = [4, 2, 1, 3];
numbers.sort((a, b) => a - b);
console.log(numbers); // Output: [1, 2, 3, 4]

const str = '42';
const num = parseInt(str, 10);
console.log(num); // Output: 42

const arr1 = [1, 2];
const arr2 = [3, 4];
const merged = arr1.concat(arr2);
console.log(merged); // Output: [1, 2, 3, 4]

const words = ['Hello', 'World'];
const sentence = words.join(' ');
console.log(sentence); // Output: 'Hello World'

const arr = [1, 2, 3, 4, 5];
arr.fill(0, 1, 4);
console.log(arr); // Output: [1, 0, 0, 0, 5]

const str = 'hello';
const letters = Array.from(str);
console.log(letters); // Output: ['h', 'e', 'l', 'l', 'o']

const obj1 = {a: 1};
const obj2 = {b: 2};
console.log(Object.assign({}, obj1, obj2)); // Output: {a: 1, b: 2}

const obj = {a: 1};
Object.freeze(obj);
obj.a = 2;
console.log(obj.a); // Output: 1

const numbers = [1, 2, 3];
const copy = [...numbers];
console.log(copy); // Output: [1, 2, 3]

const a = [1, 2];
const b = [3, 4];
const merged = [...a, ...b];
console.log(merged); // Output: [1, 2, 3, 4]

const obj = {a: 1, b: {c: 2}};
const copy = JSON.parse(JSON.stringify(obj));
console.log(copy); // Output: {a: 1, b: {c: 2}}

const numbers = [1, 2, 2, 3, 4, 4, 5];
const uniqueNumbers = new Set(numbers);
console.log(uniqueNumbers); // Output: Set {1, 2, 3, 4, 5}

const uniqueNumbers = new Set([1, 2, 3]);
const array = [...uniqueNumbers];
console.log(array); // Output: [1, 2, 3]

const person = { name: 'Alice' };
Object.freeze(person);
person.name = 'Bob';
console.log(person.name); // Output: 'Alice'

const obj1 = { a: 1 };
const obj2 = { b: 2 };
const merged = { ...obj1, ...obj2 };
console.log(merged); // Output: { a: 1, b: 2 }

const numbers = [1, 2, 3];
numbers.forEach(num => console.log(num));
// Output: 1, 2, 3

const colors = ['red', 'blue', 'green'];
for (const color of colors) {
  console.log(color);
}
// Output: 'red', 'blue', 'green'

const numbers = [10, 20, 30];
for (let i = 0; i < numbers.length; i++) {
  console.log(numbers[i]);
}
// Output: 10, 20, 30

const user = { name: 'Alice', age: 25 };
for (const key in user) {
  console.log(`${key}: ${user[key]}`);
}
// Output: 'name: Alice', 'age: 25'

const numbers = [1, 2, 3, 4, 5];
numbers.sort(() => Math.random() - 0.5);
console.log(numbers); // Output: [3, 5, 1, 4, 2] (example)

const numbers = [1, 2, 3, 4];
numbers.reverse();
console.log(numbers); // Output: [4, 3, 2, 1]

const text = 'Hello World';
console.log(text.replace('World', 'JavaScript'));
// Output: 'Hello JavaScript'

const word = 'hello';
console.log(word.split(''));
// Output: ['h', 'e', 'l', 'l', 'o']

const numbers = [10, 20, 30];
numbers.splice(1, 1);
console.log(numbers); // Output: [10, 30]

const a = [1, 2];
const b = [3, 4];
console.log([...a, ...b]); // Output: [1, 2, 3, 4]

const divs = document.querySelectorAll('div');
const divArray = Array.from(divs);
console.log(divArray); // Output: [div, div, div...]
